mapper.select:
SELECT 

FROM bas_check_system_sub a

WHERE a.id = #{id}

SELECT 

FROM bas_check_system_sub a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM bas_check_system_sub a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE bas_check_system_sub SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
