mapper.select:
SELECT 

FROM bas_ent a

WHERE a.id = #{id}

SELECT 

FROM bas_ent a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.name LIKE concat(#{name},'%')


		AND a.legal_person_name LIKE concat(#{legalPersonName},'%')


		AND a.area_code =#{areaCode}


		AND a.industry_type_code =#{industryTypeCode}


		AND find_in_set(a.id,#{ids})




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM bas_ent a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE bas_ent SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
