mapper.select:
SELECT

FROM bas_item a

WHERE a.id = #{id}

SELECT

FROM bas_item a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.parent_id = #{parent.id}


		AND a.item_type_code = #{itemTypeCode}


		AND a.name LIKE "%"#{name}"%"




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT

FROM bas_item a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE bas_item SET
del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

UPDATE bas_item SET
del_flag = '1'
WHERE parent_id = #{parentId}
AND id NOT IN 

	#{id}
