mapper.select:
SELECT 

FROM bus_plan a

WHERE a.id = #{id}

SELECT 

FROM bus_plan a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.plan_year = '${planYear}'




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM bus_plan a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE bus_plan SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
