mapper.select:
SELECT 

FROM bus_programme a

WHERE a.id = #{id}

SELECT 

FROM bus_programme a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.plan_year = #{planYear}


		AND a.company_name LIKE concat('%',#{companyName},'%')


		AND a.state = #{state}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM bus_programme a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE bus_programme SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
