mapper.select:
SELECT 

FROM bus_task a

WHERE a.id = #{id}

SELECT 

FROM bus_task a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.task_name LIKE concat(#{taskName},'%')


		AND a.task_year = #{taskYear}


		AND a.task_level = #{taskLevel}


		and a.begin_date =  #{beginDate}


		and a.end_date =  #endDate}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM bus_task a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE bus_task SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
